-- ----------------------------
-- Function structure for getChildLst
-- ----------------------------
DROP FUNCTION IF EXISTS `getChildLst`;
DELIMITER ;;
CREATE DEFINER=`admin`@`%` FUNCTION `getChildLst`(rootId varchar(32)) RETURNS varchar(1000) CHARSET utf8
BEGIN
   DECLARE sTemp VARCHAR(1000);
   DECLARE sTempChd VARCHAR(1000);

   SET sTemp = CONCAT('parent[',rootId,']');
   SET sTempChd =rootId;

   WHILE sTempChd is not null DO
     SET sTemp = concat(sTemp,',',sTempChd);
     SELECT group_concat(id) INTO sTempChd FROM productcategory where FIND_IN_SET(parent_id,sTempChd)>0;
   END WHILE;
   RETURN sTemp;
 END
;;
DELIMITER ;
